mapper.select:
select qq_messages.answer , count(qq_messages.message_id) as resultCount from qq_messages join qq_index ON qq_index.message_id = qq_messages.message_id where word in
#{item}
and ( qq_messages.owner = #{owner} or qq_messages.PRIVATABLE = 0 ) group by qq_messages.answer order by resultCount desc

mapper.insert:
insert into qq_messages (
