,   unknown
) values (
    #{qq}
,   #{owner}
,   #{message}
,   #{answer}
,   #{privatable}
,   #{unknown}
)

    SELECT @@IDENTITY

insert into qq_index (
    word
,   message_id
) values (
    #{word}
